package basic;
/*
 * display 1 to 25 numbers
 */

public class ForDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j;
		for(i=1;i<=25;i++)
		{
			System.out.println(i);
		}
		System.out.println("********** Display 55 -20 **********");
	       
        for(j=55;j>=20;j--)
        {
            System.out.println(j);
        }
	}

}
